const data = [
  { id: 1,calendar:"Takvime Ekle", name: "İlker Gümüşoluk Tek Kişilik Gösteri",src:"data:image/jpeg;base64,",text:"15 Yıldır Türkiye’nin her köşesinde 1000’den fazla gösteri yapan İlker Gümüşoluk, tek kişilik gösterisinde ikili ilişkiler başta olmak üzere, hayata dair birçok hikaye ve tespitini seyircisiyle buluşturuyor." },
  { id: 2,calendar:"Takvime Ekle", name: "Gökhan Ünver Tek Kişilik Gösteri",src:"data:image/jpeg;base64,",text:"Sahneye adım attıktan sonra reklamcılığı bırakan Gökhan Ünver; sosyal medya, kültür çatışmaları, evliliğin zor yanları, ebeveynlik gibi A’dan Z’ye bütün esprilerini açık yüreklilikle sahneye taşıyor. " },
  { id: 3,calendar:"Takvime Eklendi", name: "7 Kocalı Hürmüz",src:"data:image/jpeg;base64, ",text:" Evvel zaman içinde kalbur saman içinde , periler diyarında değil , şu güzel İstanbul'umuz da ve çok değil bundan 100 sene kadar önce yedi kocalı bir Hürmüz hanım varmış diyerek başlayan, şimdilik 5 ancak oyun " },
  { id: 4,calendar:"Takvime Ekle", name: "Bilinmeyen Bir Kadının Mektubu",src:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT4d_L3ULn4k2ftoNqJ2VjjTfeXcL9XcB4gqA&usqp=CAU",text:"Stefan Zweig’ın ölümsüz eseri “Bilinmeyen Bir Kadının Mektubu” adlı romanı muhteşem performansıyla Çağdaş Serter tarafından sahnelere taşınıyor." },
  


];


function App() {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true); 
  const [name, setName] = useState('');

  // the search result
  const [foundUsers, setFoundUsers] = useState(data);

  const filter = (e:any) => {
    const keyword = e.target.value;

    if (keyword !== '') {
      const results = data.filter((user) => {
        return user.name.toLowerCase().startsWith(keyword.toLowerCase());
        // Use the toLowerCase() method to make it case-insensitive
      });
      setFoundUsers(results);
    } else {
      setFoundUsers(data);
      // If the text field is empty, show all users
    }

    setName(keyword);
  };
  return (
    <div  >
      <img src={logo} alt="Logo"  style={{ display: "block",
 marginLeft: "auto",
  marginRight: "auto"}}/>
       <h2 style={{textAlign:"center",color:"black"}} >
        Etkinlikler
      </h2>
        <Navbar bg="light" expand="lg">
      <Container fluid>
        
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          <Nav
            className="me-auto my-2 my-lg-0"
            style={{ maxHeight: '100px' ,margin:"auto"}}
            navbarScroll
          >
            <Nav.Link href="" style={{color:"#FF3390"}}>Tüm Etkinlikler</Nav.Link>
            <Nav.Link href="">Tiyatro</Nav.Link>
            <Nav.Link href="">Konser</Nav.Link>
            <Nav.Link href="">Stand up</Nav.Link>
            <Nav.Link href="">Sinema</Nav.Link>
            <Nav.Link href="">Çocuk</Nav.Link>
          </Nav>
          <Form className="d-flex">
            <Form.Control
              type="search"
              placeholder="Etkinlik Ara"
             
              value={name}
        onChange={filter}
        className="input"
              aria-label="Search"
            />
           
          </Form>
        </Navbar.Collapse>
      </Container>
    </Navbar>
   
    {foundUsers && foundUsers.length > 0 ? (
          foundUsers.map((user) => (
    <Card style={{ width: '70%',margin:"auto", marginTop:"20px" }}>
       <Container>
       <Row>
        <Col>    
        <Card.Img style={{width:"200px",marginTop:"5%",height:"150px"}}  src={user.src} />
        </Col>
        <Col> 
         <Card.Title>{user.name}</Card.Title>
         <Card.Text style={{color:"gray"}} >Maxium UNIQ Hall</Card.Text>
         <Card.Text>
         {user.text}
         </Card.Text>
        </Col>
        <Col>
        <Button variant="danger" style={{marginTop:"15px",backgroundColor:"#FF3390"}} >Bilet Al</Button>    
         <br />
         <Button variant="light" style={{marginTop:"15px"}}>    <AiOutlinePlusCircle />{user.calendar}
</Button>
        </Col>
      </Row>
      </Container>
  </Card>
  ))
  ) : (
    <h1>No results found!</h1>
  )}
</div>
  );
}

export default App;
